import Layout from '../../components/layout';
import { useSelector, shallowEqual } from 'react-redux';
import { getPostById } from '../../lib/posts';

const usePosts = () => {
  return useSelector(
    (state) => ({
      posts: state.posts,
    }),
    shallowEqual
  );
};

export default function Post({ postData }) {
  console.log(postData);
  return (
    <Layout>
      <h1>One Post</h1>
    </Layout>
  );
}

