import React from 'react'
import {
  useDispatch
} from 'react-redux'
import {
  withRedux
} from '../lib/redux'
import useInterval from '../lib/useInterval'
import Clock from '../components/clock'

const IndexPage = () => {
  // Tick the time every second
  const dispatch = useDispatch()
  useInterval(() => {
    dispatch({
      type: 'TICK',
      light: true,
      lastUpdate: Date.now(),
    })
  }, 1000)
  return ( <
