  };

  return (
    <Layout title="The GL Blog">
      <h1>Edit Page</h1>
      <input
        placeholder="Введите email"
        id="email"
        type="text"
        name="email"
        value={form.email}
        className="yellow-input"
        onChange={changeHandler}
      />
      <input
        placeholder="Введите email"
        id="email"
        type="text"
        name="email"
        value={form.email}
        className="yellow-input"
        onChange={changeHandler}
      />
    </Layout>
  );
};

export default New;
