import {
  createStore,
  applyMiddleware
} from 'redux'
import {
  composeWithDevTools
} from 'redux-devtools-extension'
import {
  reducers
} from './posts'



const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'TICK':
      return {
        ...state,
        lastUpdate: action.lastUpdate,
          light: !!action.light,
      }
      case 'INCREMENT':
        return {
          ...state,
          count: state.count + 1,
        }
        case 'DECREMENT':
          return {
            ...state,
            count: state.count - 1,
          }
          case 'RESET':
            return {
              ...state,
              count: initialState.count,
            }
            default:
              return state
  }
}

export const initializeStore = () => {
  return createStore(
    reducer,
    composeWithDevTools(applyMiddleware())
  )
}
