import types from './types'

const init = {
  posts: [],
  post: {}
}

function postsReducer(state = init, {
  type,
  payload
}) {
  switch (type) {
    case types.GET_ALL_POSTS:
      return {
        posts: [...payload]
      };
    case types.GET_POST:
      return {
        post: payload
      };
    default:
      return state;
  }
}

export default postsReducer
