import React, { useState } from 'react';
import Layout from '../../components/Layout';
import styled from 'styled-components';

const Content = styled.div`
  margin: 0 auto;
  color: #fff;
  text-align: center;
  padding: 3rem 0;
  margin-bottom: 2rem;
`;

const Form = styled.form`
  display: block;
  padding: 0.5rem 1rem;
  border: 1px solid #000;
  line-height: 1em;
  border-radius: 10px;
  font-size: 1rem;
`;

const Input = styled.input`
  display: block;
  padding: 0.5rem 1rem;
  border: 1px solid #000;
  line-height: 1em;
  border-radius: 10px;
  font-size: 1rem;
`;

const Button = styled.button`
  display: block;
  background: #313b3f;
  padding: 0.5rem 1rem;
  border: 1px solid #fff;
  color: #fff;
  line-height: 1em;
  border-radius: 10px;
  font-size: 1rem;
`;

const New = () => {
  const [post, setPost] = useState({ title: '', body: '' });

  const changeHandler = (e) => {
    setPost({ ...post, [e.target.name]: e.target.value });
  };

  const submitHandler = () => {
    try {
      console.log(post);
      setPost({ title: '', body: '' });
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <Layout title="The GL Blog">
      <Content>
        <h1>Add new post</h1>
        <Form>
          <Input
            placeholder="Enter title"
            type="text"
            name="title"
            value={post.title}
            onChange={changeHandler}
          />

          <Input
            placeholder="Enter body"
            type="text"
            name="body"
            value={post.body}
            onChange={changeHandler}
          />

          <Button onClick={submitHandler}>Enter</Button>
        </Form>
      </Content>
    </Layout>
  );
};

export default New;
